<script setup>
import {ref, watch} from "vue";
import router from "@/router";
import {store} from "@/utils/store";
import {useDisconnect} from "@web3modal/solana/vue";
import {setTing} from "@/utils/change";

const { disconnect } = useDisconnect();

const userAddress = ref("Please log in first");

const account = ref("");
const headPic = ref('')

const closeWallet = async () => {
  store.changeLogin();
  localStorage.removeItem("headPic");
  localStorage.removeItem("Account");
  localStorage.removeItem("token");
  localStorage.removeItem("userAddress");
  localStorage.removeItem("userSigner");
  localStorage.removeItem("userName");
  disconnect();
  emit("pageValue",true);
};

const props = defineProps({
  pageVisible: Boolean,
});

const emit = defineEmits(["pageValue","openSeting"]);

const openPage = (item) => {
  router.push({ name: item });
};

const closePage = () => {
  emit("pageValue");
};

const openSet = () => {
  setTing.changeSet()
}

watch(
  () => store.loginToken,
  (newVal, oldVal) => {
    if (newVal) {
      account.value = localStorage.getItem("userName");
      headPic.value = localStorage.getItem("headPic");
    }
  }
);
</script>

